using Host.Desktop.Theme;
using Prism.Modularity;

namespace Host.Desktop.Themes
{
    public sealed class FlatLightThemeModule : IModule
    {
        private readonly IThemeService _themeService;

        public FlatLightThemeModule(IThemeService themeService)
        {
            _themeService = themeService;
        }

        public void Initialize()
        {
            _themeService.RegisterTheme("Light Grey",
                "pack://application:,,,/Host.Desktop.Themes;component/Themes/FlatLight.xaml",
                "");
        }
    }
}
